#include <bits/stdc++.h>using namespace std;void check(string s) {	     int i=0;	     int consec=1;	     int maxi=0;	     while(i<s.size()) {	     	while(i<s.size() && s[i]=='<') {	     		consec++;	     		i++;			 }			 maxi=max(maxi,consec);			 consec=1;			 while(i<s.size() && s[i]=='>') {			 	consec++;			 	i++;			 }			 			 maxi=max(maxi,consec);			 consec=1;	     			 }		 cout<<maxi<<endl;		  	   	}int main() {		int t;	cin>>t;	while(t--)  {			int n;	cin>>n;	string s;	cin>>s;				check(s);	}	}