#include <bits/stdc++.h>using namespace std;void check(vector<long long> &arr,int k) {	       sort(arr.begin(),arr.end());	      int cons=1;	      int maxi=1;	      for(int i=1; i<arr.size(); i++) {	      	  if(arr[i]-arr[i-1] <= k) {	      	  	cons++;	      	  	maxi=max(maxi,cons);				}				else {					cons=1;				}		  }		  maxi=max(maxi,cons);		  int rem=arr.size()-maxi;					  	cout<<rem<<endl;		  	   	}int main() {		int t;	cin>>t;	while(t--)  {			  long long n,k;	  cin>>n>>k;	  vector<long long> arr(n);	  for(int i=0; i<n; i++) {	  	cin>>arr[i];	  }				check(arr,k);	}	}