#include <bits/stdc++.h>using namespace std;void check(vector<int> &arr) {	                      if((arr.size()%2) ==0)   {        	   cout<<2<<endl;        	   cout<<1<<' '<<arr.size()<<endl;        	   cout<<1<<' '<<arr.size()<<endl;        	           	   		}		else {						cout<<4<<endl;			cout<<1<<' '<<arr.size()<<endl;					cout<<2<<' '<<arr.size()<<endl;			cout<<1<<' '<<2<<endl;			cout<<1<<' '<<2<<endl;		}   	}int main() {		int t;	cin>>t;	while(t--)  {				int n;		cin>>n;		vector<int> arr(n);		for(int i=0; i<n; i++) {			cin>>arr[i];		}				check(arr);	}	}