#include <bits/stdc++.h> using namespace std; void check(long long n,long long k,long long x) {      if(k*(k+1)/2 > x) {      	 cout<<"NO"<<endl;      	 return;      		  }	  long long max_sum=0;	  long long temp=k;	  long long num=n;	 max_sum= (n*(n+1)/2)  - (n-k)*(n-k+1)/2;	     if(max_sum < x) {	     	cout<<"NO"<<endl;	     	return;		 }	  	 cout<<"YES"<<endl;     	}int main() {		int t;	cin>>t;	while(t--)  {				long long n,k,x;		cin>>n>>k>>x;							check(n,k,x);	}	}