#include <bits/stdc++.h> using namespace std; void check(string x,int k) {       int cnt=0;       unordered_map<char,int> mp;       for(int i=0; i<x.size(); i++) {       	   if(mp.count(x[i])) {       	   	      mp[x[i]]--;       	   	      if(mp[x[i]]==0) {       	   	      	mp.erase(x[i]);						}			  }			else {				mp[x[i]]++;			}	   }	   if(((int)mp.size() -1) <=k) {	   	cout<<"YES"<<endl;	   }	   else {	   	cout<<"NO"<<endl;	   }              	}int main() {		int t;	cin>>t;	while(t--)  {				int n,k;		cin>>n>>k;		string x;		cin>>x;						check(x,k);	}	}