#include <bits/stdc++.h>Â using namespace std;Â void check(int a,int b,int xk,int yk,int xq,int yq) {       int cnt=0;       set<pair<int,int>> st;       st.insert({xk-a,yk-b});       st.insert({xk-a,yk+b});              st.insert({xk-b,yk-a});       st.insert({xk-b,yk+a});       st.insert({xk+b,yk+a});        st.insert({xk+b,yk-a});       st.insert({xk+a,yk+b});       st.insert({xk+a,yk-b});	   	   if(st.find({xq-a,yq-b}) !=st.end()) {	   	cnt++;	   	st.erase({xq-a,yq-b});	   }	   if(st.find({xq-a,yq+b}) != st.end()) {	   	cnt++;	   	st.erase({xq-a,yq+b});	   }	   if(st.find({xq+a,yq+b}) != st.end()) {	   	cnt++;	   	st.erase({xq+a,yq+b});	   }	   if(st.find({xq+a,yq-b}) != st.end()) {	   	cnt++;	   	st.erase({xq+a,yq-b});	   }	   if(st.find({xq-b,yq-a}) != st.end()) {	   	cnt++;	   	st.erase({xq-b,yq-a});	   }	   if(st.find({xq-b,yq+a}) != st.end()) {	   	cnt++;	   	st.erase({xq-b,yq+a});	   }	   if(st.find({xq+b,yq+a}) != st.end()) {	   	cnt++;	   	st.erase({xq+b,yq+a});	   }	   if(st.find({xq+b,yq-a}) != st.end()) {	   	cnt++;	   	st.erase({xq+b,yq-a});	   }	  	   cout<<cnt<<endl;	}int main() {		int t;	cin>>t;	while(t--)  {				int a,b,xk,yk,xq,yq;		cin>>a>>b>>xk>>yk>>xq>>yq;						check(a,b,xk,yk,xq,yq);	}	}